import React, { useState, useEffect } from 'react';
import { createClient } from '@supabase/supabase-js';
import { format } from 'date-fns';
import { nl } from 'date-fns/locale';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import './App.css';

// Supabase configuratie
const supabaseUrl = process.env.REACT_APP_SUPABASE_URL;
const supabaseKey = process.env.REACT_APP_SUPABASE_KEY;
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [activeTab, setActiveTab] = useState('inspectie');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [modalTitle, setModalTitle] = useState('');

  // Form state
  const [formData, setFormData] = useState({
    relatienummer: '',
    klantnaam: '',
    contactpersoon: '',
    contact_email: '',
    inspecteur: '',
    datum: format(new Date(), 'yyyy-MM-dd'),
    tijd: format(new Date(), 'HH:mm'),
    algemeen_values: {}
  });

  // Data state
  const [mattenData, setMattenData] = useState([]);
  const [wissersData, setWissersData] = useState([]);
  const [todoList, setTodoList] = useState([]);
  const [klantenserviceTodoList, setKlantenserviceTodoList] = useState([]);

  // Helper functies
  const formatNaam = (voornaam, tussenvoegsel, achternaam) => {
    const v = voornaam && voornaam !== 'None' ? voornaam : '';
    const t = tussenvoegsel && tussenvoegsel !== 'None' ? tussenvoegsel : '';
    const a = achternaam && achternaam !== 'None' ? achternaam : '';
    return `${v} ${t} ${a}`.replace(/\s+/g, ' ').trim();
  };

  const berekenLeeftijd = (barcode) => {
    if (!barcode || barcode.toString().length < 7) {
      return '-';
    }

    try {
      const barcodeStr = barcode.toString().trim();

      if (barcodeStr.length >= 7) {
        const maandDigit = barcodeStr[4];
        const jaarDigits = barcodeStr.slice(5, 7);

        const maand = parseInt(maandDigit);
        const jaar = parseInt(jaarDigits);

        if (maand < 1 || maand > 12) {
          return `Onbekend (maand ${maand} ongeldig)`;
        }

        const volledigJaar = 2000 + jaar;
        const productieDatum = new Date(volledigJaar, maand - 1, 1);
        const vandaag = new Date();
        const leeftijdDagen = Math.floor((vandaag - productieDatum) / (1000 * 60 * 60 * 24));
        const leeftijdMaanden = Math.floor(leeftijdDagen / 30);

        if (leeftijdMaanden < 0) {
          return 'Onbekend (toekomstige datum)';
        }

        if (leeftijdMaanden < 12) {
          return `${leeftijdMaanden} maanden`;
        } else {
          const leeftijdJaren = Math.floor(leeftijdMaanden / 12);
          const resterendeMaanden = leeftijdMaanden % 12;
          if (resterendeMaanden === 0) {
            return `${leeftijdJaren} jaar`;
          } else {
            return `${leeftijdJaren} jaar en ${resterendeMaanden} maanden`;
          }
        }
      } else {
        return 'Onbekend (te kort)';
      }
    } catch (e) {
      return `Onbekend (fout: ${e})`;
    }
  };

  const toBool = (val) => {
    if (typeof val === 'boolean') return val;
    if (typeof val === 'string') {
      const v = val.trim().toLowerCase();
      if (['true', 'ja', '1', 'yes'].includes(v)) return true;
      if (['false', 'nee', '0', 'no', ''].includes(v)) return false;
      return false;
    }
    if (typeof val === 'number') return val === 1;
    return false;
  };

  const boolToJaNee = (val) => {
    return val ? 'Ja' : 'Nee';
  };

  const showMessage = (text, type = 'info') => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 5000);
  };

  const loadAbonnementen = async (soort) => {
    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('abonnementen')
        .select('*')
        .eq('soort', soort);

      if (error) throw error;

      if (soort === 'matten') {
        setMattenData(data || []);
      } else {
        setWissersData(data || []);
      }
    } catch (error) {
      showMessage(`Fout bij laden van ${soort}: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const saveInspectie = async () => {
    try {
      setLoading(true);

      const inspectieData = {
        relatienummer: formData.relatienummer,
        klantnaam: formData.klantnaam,
        contactpersoon: formData.contactpersoon,
        contact_email: formData.contact_email,
        inspecteur: formData.inspecteur,
        datum: formData.datum,
        tijd: formData.tijd,
        matten_data: mattenData,
        wissers_data: wissersData,
        algemeen_values: formData.algemeen_values,
        created_at: new Date().toISOString()
      };

      const { error } = await supabase
        .from('inspecties')
        .insert(inspectieData);

      if (error) throw error;

      showMessage('Inspectie succesvol opgeslagen!', 'success');

      // Genereer to-do's
      generateTodos();

    } catch (error) {
      showMessage(`Fout bij opslaan: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const generateTodos = () => {
    const serviceTodos = [];
    const klantenserviceTodos = [];

    // Analyseer matten data
    mattenData.forEach(mat => {
      if (!toBool(mat.aanwezig)) {
        serviceTodos.push(`Mat ${mat.productnummer} niet aanwezig - vervangen`);
      }
      if (!toBool(mat.schoon_onbeschadigd)) {
        serviceTodos.push(`Mat ${mat.productnummer} reinigen of vervangen`);
      }
    });

    // Analyseer wissers data
    wissersData.forEach(wisser => {
      if (!toBool(wisser.aanwezig)) {
        serviceTodos.push(`Wisser ${wisser.productnummer} niet aanwezig - vervangen`);
      }
      if (!toBool(wisser.schoon_onbeschadigd)) {
        serviceTodos.push(`Wisser ${wisser.productnummer} reinigen of vervangen`);
      }
    });

    setTodoList(serviceTodos.map(text => ({ text, done: false })));
    setKlantenserviceTodoList(klantenserviceTodos.map(text => ({ text, done: false })));
  };

  const generatePDF = async () => {
    try {
      setLoading(true);

      const element = document.getElementById('rapport-content');
      const canvas = await html2canvas(element);
      const imgData = canvas.toDataURL('image/png');

      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210;
      const pageHeight = 295;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      let heightLeft = imgHeight;

      let position = 0;

      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }

      pdf.save(`rapport_${formData.relatienummer}_${format(new Date(), 'yyyyMMdd')}.pdf`);
      showMessage('PDF succesvol gegenereerd!', 'success');

    } catch (error) {
      showMessage(`Fout bij genereren PDF: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const updateMatData = (index, field, value) => {
    const newData = [...mattenData];
    newData[index] = { ...newData[index], [field]: value };
    setMattenData(newData);
  };

  const updateWisserData = (index, field, value) => {
    const newData = [...wissersData];
    newData[index] = { ...newData[index], [field]: value };
    setWissersData(newData);
  };

  const updateAlgemeenValue = (key, value) => {
    setFormData(prev => ({
      ...prev,
      algemeen_values: {
        ...prev.algemeen_values,
        [key]: value
      }
    }));
  };

  useEffect(() => {
    loadAbonnementen('matten');
    loadAbonnementen('wissers');
  }, []);

  const renderInspectieForm = () => (
    <div className="card">
      <h2>Inspectie Formulier</h2>

      <div className="form-group">
        <label>Relatienummer:</label>
        <input
          type="text"
          className="form-control"
          value={formData.relatienummer}
          onChange={(e) => setFormData({...formData, relatienummer: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Klantnaam:</label>
        <input
          type="text"
          className="form-control"
          value={formData.klantnaam}
          onChange={(e) => setFormData({...formData, klantnaam: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Contactpersoon:</label>
        <input
          type="text"
          className="form-control"
          value={formData.contactpersoon}
          onChange={(e) => setFormData({...formData, contactpersoon: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Contact Email:</label>
        <input
          type="email"
          className="form-control"
          value={formData.contact_email}
          onChange={(e) => setFormData({...formData, contact_email: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Inspecteur:</label>
        <input
          type="text"
          className="form-control"
          value={formData.inspecteur}
          onChange={(e) => setFormData({...formData, inspecteur: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Datum:</label>
        <input
          type="date"
          className="form-control"
          value={formData.datum}
          onChange={(e) => setFormData({...formData, datum: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Tijd:</label>
        <input
          type="time"
          className="form-control"
          value={formData.tijd}
          onChange={(e) => setFormData({...formData, tijd: e.target.value})}
        />
      </div>

      <div className="card">
        <h3>Algemeen</h3>
        <div className="form-group">
          <label>Opmerkingen:</label>
          <textarea
            className="form-control"
            rows="3"
            value={formData.algemeen_values.opmerkingen || ''}
            onChange={(e) => updateAlgemeenValue('opmerkingen', e.target.value)}
          />
        </div>
      </div>

      <div className="card">
        <h3>Matten</h3>
        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            Laden...
          </div>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>Productnummer</th>
                <th>Locatie</th>
                <th>Aanwezig</th>
                <th>Schoon/Onbeschadigd</th>
                <th>Leeftijd</th>
                <th>Opmerkingen</th>
              </tr>
            </thead>
            <tbody>
              {mattenData.map((mat, index) => (
                <tr key={index}>
                  <td>{mat.productnummer}</td>
                  <td>{mat.locatie}</td>
                  <td>
                    <div className="checkbox-group">
                      <input
                        type="checkbox"
                        checked={toBool(mat.aanwezig)}
                        onChange={(e) => updateMatData(index, 'aanwezig', e.target.checked)}
                      />
                      <span>{boolToJaNee(toBool(mat.aanwezig))}</span>
                    </div>
                  </td>
                  <td>
                    <div className="checkbox-group">
                      <input
                        type="checkbox"
                        checked={toBool(mat.schoon_onbeschadigd)}
                        onChange={(e) => updateMatData(index, 'schoon_onbeschadigd', e.target.checked)}
                      />
                      <span>{boolToJaNee(toBool(mat.schoon_onbeschadigd))}</span>
                    </div>
                  </td>
                  <td>{berekenLeeftijd(mat.barcode)}</td>
                  <td>
                    <input
                      type="text"
                      className="form-control"
                      value={mat.opmerkingen || ''}
                      onChange={(e) => updateMatData(index, 'opmerkingen', e.target.value)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div className="card">
        <h3>Wissers</h3>
        {loading ? (
          <div className="loading">
            <div className="spinner"></div>
            Laden...
          </div>
        ) : (
          <table className="table">
            <thead>
              <tr>
                <th>Productnummer</th>
                <th>Locatie</th>
                <th>Aanwezig</th>
                <th>Schoon/Onbeschadigd</th>
                <th>Routecontact</th>
                <th>Nog in dienst</th>
                <th>Opmerkingen</th>
              </tr>
            </thead>
            <tbody>
              {wissersData.map((wisser, index) => (
                <tr key={index}>
                  <td>{wisser.productnummer}</td>
                  <td>{wisser.locatie}</td>
                  <td>
                    <div className="checkbox-group">
                      <input
                        type="checkbox"
                        checked={toBool(wisser.aanwezig)}
                        onChange={(e) => updateWisserData(index, 'aanwezig', e.target.checked)}
                      />
                      <span>{boolToJaNee(toBool(wisser.aanwezig))}</span>
                    </div>
                  </td>
                  <td>
                    <div className="checkbox-group">
                      <input
                        type="checkbox"
                        checked={toBool(wisser.schoon_onbeschadigd)}
                        onChange={(e) => updateWisserData(index, 'schoon_onbeschadigd', e.target.checked)}
                      />
                      <span>{boolToJaNee(toBool(wisser.schoon_onbeschadigd))}</span>
                    </div>
                  </td>
                  <td>
                    <div className="checkbox-group">
                      <input
                        type="checkbox"
                        checked={toBool(wisser.routecontact)}
                        onChange={(e) => updateWisserData(index, 'routecontact', e.target.checked)}
                      />
                      <span>{boolToJaNee(toBool(wisser.routecontact))}</span>
                    </div>
                  </td>
                  <td>
                    <div className="checkbox-group">
                      <input
                        type="checkbox"
                        checked={toBool(wisser.nog_in_dienst)}
                        onChange={(e) => updateWisserData(index, 'nog_in_dienst', e.target.checked)}
                      />
                      <span>{boolToJaNee(toBool(wisser.nog_in_dienst))}</span>
                    </div>
                  </td>
                  <td>
                    <input
                      type="text"
                      className="form-control"
                      value={wisser.opmerkingen || ''}
                      onChange={(e) => updateWisserData(index, 'opmerkingen', e.target.value)}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div style={{ marginTop: '20px' }}>
        <button className="btn btn-primary" onClick={saveInspectie} disabled={loading}>
          {loading ? 'Opslaan...' : 'Inspectie Opslaan'}
        </button>
        <button className="btn btn-secondary" onClick={generatePDF} disabled={loading} style={{ marginLeft: '10px' }}>
          {loading ? 'Genereren...' : 'PDF Genereren'}
        </button>
      </div>
    </div>
  );

  const renderTodoList = () => (
    <div className="card">
      <h2>To-Do Lijsten</h2>

      <div className="card">
        <h3>Service To-Do's</h3>
        <ul>
          {todoList.map((todo, index) => (
            <li key={index} style={{ textDecoration: todo.done ? 'line-through' : 'none' }}>
              <input
                type="checkbox"
                checked={todo.done}
                onChange={(e) => {
                  const newList = [...todoList];
                  newList[index].done = e.target.checked;
                  setTodoList(newList);
                }}
              />
              {todo.text}
            </li>
          ))}
        </ul>
      </div>

      <div className="card">
        <h3>Klantenservice To-Do's</h3>
        <ul>
          {klantenserviceTodoList.map((todo, index) => (
            <li key={index} style={{ textDecoration: todo.done ? 'line-through' : 'none' }}>
              <input
                type="checkbox"
                checked={todo.done}
                onChange={(e) => {
                  const newList = [...klantenserviceTodoList];
                  newList[index].done = e.target.checked;
                  setKlantenserviceTodoList(newList);
                }}
              />
              {todo.text}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );

  const renderRapport = () => (
    <div className="card">
      <h2>Rapport</h2>
      <div id="rapport-content">
        <h1>Lavans Service Rapport</h1>
        <p><strong>Relatienummer:</strong> {formData.relatienummer}</p>
        <p><strong>Klantnaam:</strong> {formData.klantnaam}</p>
        <p><strong>Contactpersoon:</strong> {formData.contactpersoon}</p>
        <p><strong>Inspecteur:</strong> {formData.inspecteur}</p>
        <p><strong>Datum:</strong> {formData.datum}</p>
        <p><strong>Tijd:</strong> {formData.tijd}</p>

        <h3>Algemeen</h3>
        <p><strong>Opmerkingen:</strong> {formData.algemeen_values.opmerkingen || '-'}</p>

        <h3>Matten</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Productnummer</th>
              <th>Locatie</th>
              <th>Aanwezig</th>
              <th>Schoon/Onbeschadigd</th>
              <th>Leeftijd</th>
              <th>Opmerkingen</th>
            </tr>
          </thead>
          <tbody>
            {mattenData.map((mat, index) => (
              <tr key={index}>
                <td>{mat.productnummer}</td>
                <td>{mat.locatie}</td>
                <td>{boolToJaNee(toBool(mat.aanwezig))}</td>
                <td>{boolToJaNee(toBool(mat.schoon_onbeschadigd))}</td>
                <td>{berekenLeeftijd(mat.barcode)}</td>
                <td>{mat.opmerkingen || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <h3>Wissers</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Productnummer</th>
              <th>Locatie</th>
              <th>Aanwezig</th>
              <th>Schoon/Onbeschadigd</th>
              <th>Routecontact</th>
              <th>Nog in dienst</th>
              <th>Opmerkingen</th>
            </tr>
          </thead>
          <tbody>
            {wissersData.map((wisser, index) => (
              <tr key={index}>
                <td>{wisser.productnummer}</td>
                <td>{wisser.locatie}</td>
                <td>{boolToJaNee(toBool(wisser.aanwezig))}</td>
                <td>{boolToJaNee(toBool(wisser.schoon_onbeschadigd))}</td>
                <td>{boolToJaNee(toBool(wisser.routecontact))}</td>
                <td>{boolToJaNee(toBool(wisser.nog_in_dienst))}</td>
                <td>{wisser.opmerkingen || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  return (
    <div className="App">
      <div className="header">
        <h1>Lavans Service App</h1>
        <p>Ideaal Servicemoment</p>
      </div>

      <div className="container">
        {message && (
          <div className={`alert alert-${messageType}`}>
            {message}
          </div>
        )}

        <div className="tabs">
          <button
            className={`tab ${activeTab === 'inspectie' ? 'active' : ''}`}
            onClick={() => setActiveTab('inspectie')}
          >
            Inspectie
          </button>
          <button
            className={`tab ${activeTab === 'todos' ? 'active' : ''}`}
            onClick={() => setActiveTab('todos')}
          >
            To-Do's
          </button>
          <button
            className={`tab ${activeTab === 'rapport' ? 'active' : ''}`}
            onClick={() => setActiveTab('rapport')}
          >
            Rapport
          </button>
        </div>

        {activeTab === 'inspectie' && renderInspectieForm()}
        {activeTab === 'todos' && renderTodoList()}
        {activeTab === 'rapport' && renderRapport()}

        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <div className="modal-header">
                <h3>{modalTitle}</h3>
                <button className="modal-close" onClick={() => setShowModal(false)}>

                </button>
              </div>
              <div dangerouslySetInnerHTML={{ __html: modalContent }} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
