import React, { useState, useEffect } from 'react';
import { format } from 'date-fns';
import { nl } from 'date-fns/locale';
import jsPDF from 'jspdf';
import html2canvas from 'html2canvas';
import './App.css';

// Hardcoded data
const HARDCODED_MATTEN = [
  {
    productnummer: "MAT001",
    locatie: "Ingang",
    barcode: "1234567890123",
    aanwezig: true,
    schoon_onbeschadigd: true,
    concurrenten_aanwezig: false,
    opmerkingen: ""
  },
  {
    productnummer: "MAT002", 
    locatie: "Kantoor",
    barcode: "1234567890124",
    aanwezig: true,
    schoon_onbeschadigd: false,
    concurrenten_aanwezig: true,
    opmerkingen: "Vervangen nodig"
  },
  {
    productnummer: "MAT003",
    locatie: "Magazijn",
    barcode: "1234567890125", 
    aanwezig: false,
    schoon_onbeschadigd: false,
    concurrenten_aanwezig: false,
    opmerkingen: "Niet aanwezig"
  },
  {
    productnummer: "MAT004",
    locatie: "Receptie",
    barcode: "1234567890126",
    aanwezig: true,
    schoon_onbeschadigd: true,
    concurrenten_aanwezig: false,
    opmerkingen: ""
  },
  {
    productnummer: "MAT005",
    locatie: "Keuken",
    barcode: "1234567890127",
    aanwezig: true,
    schoon_onbeschadigd: false,
    concurrenten_aanwezig: true,
    opmerkingen: "Reinigen nodig"
  }
];

const HARDCODED_WISSERS = [
  {
    productnummer: "WIS001",
    locatie: "Ingang",
    aanwezig: true,
    schoon_onbeschadigd: true,
    routecontact: true,
    nog_in_dienst: true,
    concurrenten_aanwezig: false,
    opmerkingen: ""
  },
  {
    productnummer: "WIS002",
    locatie: "Kantoor",
    aanwezig: true,
    schoon_onbeschadigd: false,
    routecontact: false,
    nog_in_dienst: true,
    concurrenten_aanwezig: true,
    opmerkingen: "Vervangen nodig"
  },
  {
    productnummer: "WIS003",
    locatie: "Magazijn",
    aanwezig: false,
    schoon_onbeschadigd: false,
    routecontact: false,
    nog_in_dienst: false,
    concurrenten_aanwezig: false,
    opmerkingen: "Niet aanwezig"
  },
  {
    productnummer: "WIS004",
    locatie: "Receptie",
    aanwezig: true,
    schoon_onbeschadigd: true,
    routecontact: true,
    nog_in_dienst: true,
    concurrenten_aanwezig: false,
    opmerkingen: ""
  },
  {
    productnummer: "WIS005",
    locatie: "Keuken",
    aanwezig: true,
    schoon_onbeschadigd: false,
    routecontact: false,
    nog_in_dienst: true,
    concurrenten_aanwezig: true,
    opmerkingen: "Reinigen nodig"
  }
];

function App() {
  const [activeTab, setActiveTab] = useState('inspectie');
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [modalContent, setModalContent] = useState('');
  const [modalTitle, setModalTitle] = useState('');

  // Form state
  const [formData, setFormData] = useState({
    relatienummer: '',
    klantnaam: '',
    contactpersoon: '',
    contact_email: '',
    inspecteur: '',
    datum: format(new Date(), 'yyyy-MM-dd'),
    tijd: format(new Date(), 'HH:mm'),
    algemeen_values: {}
  });

  // Concurrenten state
  const [mattenConcurrenten, setMattenConcurrenten] = useState({
    andere_mat_aanwezig: 'Nee',
    andere_mat_concurrent: '',
    aantal_concurrent: 0,
    aantal_koop: 0
  });

  const [wissersConcurrenten, setWissersConcurrenten] = useState({
    wissers_concurrent: 'Nee',
    wissers_concurrent_toelichting: '',
    andere_zaken: ''
  });

  // Data state
  const [mattenData, setMattenData] = useState([]);
  const [wissersData, setWissersData] = useState([]);
  const [todoList, setTodoList] = useState([]);
  const [klantenserviceTodoList, setKlantenserviceTodoList] = useState([]);
  const [savedInspecties, setSavedInspecties] = useState([]);

  // Helper functies
  const formatNaam = (voornaam, tussenvoegsel, achternaam) => {
    const v = voornaam && voornaam !== 'None' ? voornaam : '';
    const t = tussenvoegsel && tussenvoegsel !== 'None' ? tussenvoegsel : '';
    const a = achternaam && achternaam !== 'None' ? achternaam : '';
    return `${v} ${t} ${a}`.replace(/\s+/g, ' ').trim();
  };

  const berekenLeeftijd = (barcode) => {
    if (!barcode || barcode.toString().length < 7) {
      return '-';
    }

    try {
      const barcodeStr = barcode.toString().trim();

      if (barcodeStr.length >= 7) {
        const maandDigit = barcodeStr[4];
        const jaarDigits = barcodeStr.slice(5, 7);

        const maand = parseInt(maandDigit);
        const jaar = parseInt(jaarDigits);

        if (maand < 1 || maand > 12) {
          return `Onbekend (maand ${maand} ongeldig)`;
        }

        const volledigJaar = 2000 + jaar;
        const productieDatum = new Date(volledigJaar, maand - 1, 1);
        const vandaag = new Date();
        const leeftijdDagen = Math.floor((vandaag - productieDatum) / (1000 * 60 * 60 * 24));
        const leeftijdMaanden = Math.floor(leeftijdDagen / 30);

        if (leeftijdMaanden < 0) {
          return 'Onbekend (toekomstige datum)';
        }

        if (leeftijdMaanden < 12) {
          return `${leeftijdMaanden} maanden`;
        } else {
          const leeftijdJaren = Math.floor(leeftijdMaanden / 12);
          const resterendeMaanden = leeftijdMaanden % 12;
          if (resterendeMaanden === 0) {
            return `${leeftijdJaren} jaar`;
          } else {
            return `${leeftijdJaren} jaar en ${resterendeMaanden} maanden`;
          }
        }
      } else {
        return 'Onbekend (te kort)';
      }
    } catch (e) {
      return `Onbekend (fout: ${e})`;
    }
  };

  const toBool = (val) => {
    if (typeof val === 'boolean') return val;
    if (typeof val === 'string') {
      const v = val.trim().toLowerCase();
      if (['true', 'ja', '1', 'yes'].includes(v)) return true;
      if (['false', 'nee', '0', 'no', ''].includes(v)) return false;
      return false;
    }
    if (typeof val === 'number') return val === 1;
    return false;
  };

  const boolToJaNee = (val) => {
    return val ? 'Ja' : 'Nee';
  };

  const showMessage = (text, type = 'info') => {
    setMessage(text);
    setMessageType(type);
    setTimeout(() => {
      setMessage('');
      setMessageType('');
    }, 5000);
  };

  const loadData = () => {
    // Laad hardcoded data
    setMattenData([...HARDCODED_MATTEN]);
    setWissersData([...HARDCODED_WISSERS]);

    // Laad opgeslagen inspecties uit localStorage
    const saved = localStorage.getItem('lavans_inspecties');
    if (saved) {
      try {
        setSavedInspecties(JSON.parse(saved));
      } catch (e) {
        console.error('Fout bij laden opgeslagen inspecties:', e);
      }
    }
  };

  const saveInspectie = async () => {
    try {
      setLoading(true);

      const inspectieData = {
        id: Date.now(),
        relatienummer: formData.relatienummer,
        klantnaam: formData.klantnaam,
        contactpersoon: formData.contactpersoon,
        contact_email: formData.contact_email,
        inspecteur: formData.inspecteur,
        datum: formData.datum,
        tijd: formData.tijd,
        matten_data: mattenData,
        wissers_data: wissersData,
        matten_concurrenten: mattenConcurrenten,
        wissers_concurrenten: wissersConcurrenten,
        algemeen_values: formData.algemeen_values,
        created_at: new Date().toISOString()
      };

      // Sla op in localStorage
      const existing = JSON.parse(localStorage.getItem('lavans_inspecties') || '[]');
      existing.push(inspectieData);
      localStorage.setItem('lavans_inspecties', JSON.stringify(existing));
      setSavedInspecties(existing);

      showMessage('Inspectie succesvol opgeslagen!', 'success');

      // Genereer to-do's
      generateTodos();

    } catch (error) {
      showMessage(`Fout bij opslaan: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const generateTodos = () => {
    const serviceTodos = [];
    const klantenserviceTodos = [];

    // Analyseer matten data
    mattenData.forEach(mat => {
      if (!toBool(mat.aanwezig)) {
        serviceTodos.push(`Mat ${mat.productnummer} niet aanwezig - vervangen`);
      }
      if (!toBool(mat.schoon_onbeschadigd)) {
        serviceTodos.push(`Mat ${mat.productnummer} reinigen of vervangen`);
      }
    });

    // Analyseer wissers data
    wissersData.forEach(wisser => {
      if (!toBool(wisser.aanwezig)) {
        serviceTodos.push(`Wisser ${wisser.productnummer} niet aanwezig - vervangen`);
      }
      if (!toBool(wisser.schoon_onbeschadigd)) {
        serviceTodos.push(`Wisser ${wisser.productnummer} reinigen of vervangen`);
      }
    });

    // Concurrenten to-do's
    if (mattenConcurrenten.andere_mat_aanwezig === 'Ja') {
      serviceTodos.push(`Concurrenten matten aanwezig: ${mattenConcurrenten.andere_mat_concurrent} - actie ondernemen`);
    }

    if (wissersConcurrenten.wissers_concurrent === 'Ja') {
      serviceTodos.push(`Concurrenten wissers aanwezig: ${wissersConcurrenten.wissers_concurrent_toelichting} - actie ondernemen`);
    }

    setTodoList(serviceTodos.map(text => ({ text, done: false })));
    setKlantenserviceTodoList(klantenserviceTodos.map(text => ({ text, done: false })));
  };

  const generatePDF = async () => {
    try {
      setLoading(true);

      const element = document.getElementById('rapport-content');
      const canvas = await html2canvas(element);
      const imgData = canvas.toDataURL('image/png');

      const pdf = new jsPDF('p', 'mm', 'a4');
      const imgWidth = 210;
      const pageHeight = 295;
      const imgHeight = (canvas.height * imgWidth) / canvas.width;
      let heightLeft = imgHeight;

      let position = 0;

      pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
      heightLeft -= pageHeight;

      while (heightLeft >= 0) {
        position = heightLeft - imgHeight;
        pdf.addPage();
        pdf.addImage(imgData, 'PNG', 0, position, imgWidth, imgHeight);
        heightLeft -= pageHeight;
      }

      pdf.save(`rapport_${formData.relatienummer}_${format(new Date(), 'yyyyMMdd')}.pdf`);
      showMessage('PDF succesvol gegenereerd!', 'success');

    } catch (error) {
      showMessage(`Fout bij genereren PDF: ${error.message}`, 'error');
    } finally {
      setLoading(false);
    }
  };

  const updateMatData = (index, field, value) => {
    const newData = [...mattenData];
    newData[index] = { ...newData[index], [field]: value };
    setMattenData(newData);
  };

  const updateWisserData = (index, field, value) => {
    const newData = [...wissersData];
    newData[index] = { ...newData[index], [field]: value };
    setWissersData(newData);
  };

  const updateAlgemeenValue = (key, value) => {
    setFormData(prev => ({
      ...prev,
      algemeen_values: {
        ...prev.algemeen_values,
        [key]: value
      }
    }));
  };

  const clearData = () => {
    if (window.confirm('Weet je zeker dat je alle opgeslagen data wilt wissen?')) {
      localStorage.removeItem('lavans_inspecties');
      setSavedInspecties([]);
      showMessage('Alle data gewist!', 'success');
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  const renderInspectieForm = () => (
    <div className="card">
      <h2>Inspectie Formulier</h2>

      <div className="form-group">
        <label>Relatienummer:</label>
        <input
          type="text"
          className="form-control"
          value={formData.relatienummer}
          onChange={(e) => setFormData({...formData, relatienummer: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Klantnaam:</label>
        <input
          type="text"
          className="form-control"
          value={formData.klantnaam}
          onChange={(e) => setFormData({...formData, klantnaam: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Contactpersoon:</label>
        <input
          type="text"
          className="form-control"
          value={formData.contactpersoon}
          onChange={(e) => setFormData({...formData, contactpersoon: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Contact Email:</label>
        <input
          type="email"
          className="form-control"
          value={formData.contact_email}
          onChange={(e) => setFormData({...formData, contact_email: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Inspecteur:</label>
        <input
          type="text"
          className="form-control"
          value={formData.inspecteur}
          onChange={(e) => setFormData({...formData, inspecteur: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Datum:</label>
        <input
          type="date"
          className="form-control"
          value={formData.datum}
          onChange={(e) => setFormData({...formData, datum: e.target.value})}
        />
      </div>

      <div className="form-group">
        <label>Tijd:</label>
        <input
          type="time"
          className="form-control"
          value={formData.tijd}
          onChange={(e) => setFormData({...formData, tijd: e.target.value})}
        />
      </div>

      <div className="card">
        <h3>Algemeen</h3>
        <div className="form-group">
          <label>Opmerkingen:</label>
          <textarea
            className="form-control"
            rows="3"
            value={formData.algemeen_values.opmerkingen || ''}
            onChange={(e) => updateAlgemeenValue('opmerkingen', e.target.value)}
          />
        </div>
      </div>

      <div className="card">
        <h3>Matten</h3>

        {/* Concurrenten sectie voor matten */}
        <div className="form-group">
          <label>Zien we matten van de concurrent liggen?</label>
          <div className="checkbox-group">
            <input
              type="radio"
              name="andere_mat_aanwezig"
              value="Nee"
              checked={mattenConcurrenten.andere_mat_aanwezig === 'Nee'}
              onChange={(e) => setMattenConcurrenten({...mattenConcurrenten, andere_mat_aanwezig: e.target.value})}
            />
            <span>Nee</span>
            <input
              type="radio"
              name="andere_mat_aanwezig"
              value="Ja"
              checked={mattenConcurrenten.andere_mat_aanwezig === 'Ja'}
              onChange={(e) => setMattenConcurrenten({...mattenConcurrenten, andere_mat_aanwezig: e.target.value})}
            />
            <span>Ja</span>
          </div>
        </div>

        {mattenConcurrenten.andere_mat_aanwezig === 'Ja' && (
          <div className="form-group">
            <label>Van welke concurrent?</label>
            <select
              className="form-control"
              value={mattenConcurrenten.andere_mat_concurrent}
              onChange={(e) => setMattenConcurrenten({...mattenConcurrenten, andere_mat_concurrent: e.target.value})}
            >
              <option value="">Selecteer concurrent</option>
              <option value="CWS">CWS</option>
              <option value="ELIS">ELIS</option>
              <option value="Quality Service">Quality Service</option>
              <option value="Vendrig">Vendrig</option>
              <option value="Mewa">Mewa</option>
              <option value="Anders">Anders namelijk:</option>
            </select>
            {mattenConcurrenten.andere_mat_concurrent === 'Anders' && (
              <input
                type="text"
                className="form-control"
                placeholder="Welke andere concurrent?"
                value={mattenConcurrenten.andere_mat_concurrent === 'Anders' ? '' : mattenConcurrenten.andere_mat_concurrent}
                onChange={(e) => setMattenConcurrenten({...mattenConcurrenten, andere_mat_concurrent: e.target.value})}
              />
            )}
            <label>Aantal matten van concurrent:</label>
            <input
              type="number"
              className="form-control"
              min="0"
              value={mattenConcurrenten.aantal_concurrent}
              onChange={(e) => setMattenConcurrenten({...mattenConcurrenten, aantal_concurrent: parseInt(e.target.value) || 0})}
            />
          </div>
        )}

        <div className="form-group">
          <label>Aantal koop matten:</label>
          <input
            type="number"
            className="form-control"
            min="0"
            value={mattenConcurrenten.aantal_koop}
            onChange={(e) => setMattenConcurrenten({...mattenConcurrenten, aantal_koop: parseInt(e.target.value) || 0})}
          />
        </div>

        <table className="table">
          <thead>
            <tr>
              <th>Productnummer</th>
              <th>Locatie</th>
              <th>Aanwezig</th>
              <th>Schoon/Onbeschadigd</th>
              <th>Barcode</th>
              <th>Leeftijd</th>
              <th>Opmerkingen</th>
            </tr>
          </thead>
          <tbody>
            {mattenData.map((mat, index) => (
              <tr key={index}>
                <td>{mat.productnummer}</td>
                <td>{mat.locatie}</td>
                <td>
                  <div className="checkbox-group">
                    <input
                      type="checkbox"
                      checked={toBool(mat.aanwezig)}
                      onChange={(e) => updateMatData(index, 'aanwezig', e.target.checked)}
                    />
                    <span>{boolToJaNee(toBool(mat.aanwezig))}</span>
                  </div>
                </td>
                <td>
                  <div className="checkbox-group">
                    <input
                      type="checkbox"
                      checked={toBool(mat.schoon_onbeschadigd)}
                      onChange={(e) => updateMatData(index, 'schoon_onbeschadigd', e.target.checked)}
                    />
                    <span>{boolToJaNee(toBool(mat.schoon_onbeschadigd))}</span>
                  </div>
                </td>
                <td>
                  <input
                    type="text"
                    className="form-control"
                    value={mat.barcode || ''}
                    onChange={(e) => updateMatData(index, 'barcode', e.target.value)}
                    placeholder="Vul barcode in (bijv. 0300522)"
                  />
                </td>
                <td>{berekenLeeftijd(mat.barcode)}</td>
                <td>
                  <input
                    type="text"
                    className="form-control"
                    value={mat.opmerkingen || ''}
                    onChange={(e) => updateMatData(index, 'opmerkingen', e.target.value)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div className="card">
        <h3>Wissers</h3>

        {/* Concurrenten sectie voor wissers */}
        <div className="form-group">
          <label>Zien we wissers van concurrenten staan?</label>
          <div className="checkbox-group">
            <input
              type="radio"
              name="wissers_concurrent"
              value="Nee"
              checked={wissersConcurrenten.wissers_concurrent === 'Nee'}
              onChange={(e) => setWissersConcurrenten({...wissersConcurrenten, wissers_concurrent: e.target.value})}
            />
            <span>Nee</span>
            <input
              type="radio"
              name="wissers_concurrent"
              value="Ja"
              checked={wissersConcurrenten.wissers_concurrent === 'Ja'}
              onChange={(e) => setWissersConcurrenten({...wissersConcurrenten, wissers_concurrent: e.target.value})}
            />
            <span>Ja</span>
          </div>
        </div>

        {wissersConcurrenten.wissers_concurrent === 'Ja' && (
          <div className="form-group">
            <label>Welke concurrent(en)?</label>
            <input
              type="text"
              className="form-control"
              value={wissersConcurrenten.wissers_concurrent_toelichting}
              onChange={(e) => setWissersConcurrenten({...wissersConcurrenten, wissers_concurrent_toelichting: e.target.value})}
            />
          </div>
        )}

        <div className="form-group">
          <label>Zie je andere schoonmaakmiddelen staan?</label>
          <textarea
            className="form-control"
            rows="3"
            value={wissersConcurrenten.andere_zaken}
            onChange={(e) => setWissersConcurrenten({...wissersConcurrenten, andere_zaken: e.target.value})}
            placeholder="Bezems, wissers van andere leveranciers, etc."
          />
        </div>

        <table className="table">
          <thead>
            <tr>
              <th>Productnummer</th>
              <th>Locatie</th>
              <th>Aanwezig</th>
              <th>Schoon/Onbeschadigd</th>
              <th>Routecontact</th>
              <th>Nog in dienst</th>
              <th>Opmerkingen</th>
            </tr>
          </thead>
          <tbody>
            {wissersData.map((wisser, index) => (
              <tr key={index}>
                <td>{wisser.productnummer}</td>
                <td>{wisser.locatie}</td>
                <td>
                  <div className="checkbox-group">
                    <input
                      type="checkbox"
                      checked={toBool(wisser.aanwezig)}
                      onChange={(e) => updateWisserData(index, 'aanwezig', e.target.checked)}
                    />
                    <span>{boolToJaNee(toBool(wisser.aanwezig))}</span>
                  </div>
                </td>
                <td>
                  <div className="checkbox-group">
                    <input
                      type="checkbox"
                      checked={toBool(wisser.schoon_onbeschadigd)}
                      onChange={(e) => updateWisserData(index, 'schoon_onbeschadigd', e.target.checked)}
                    />
                    <span>{boolToJaNee(toBool(wisser.schoon_onbeschadigd))}</span>
                  </div>
                </td>
                <td>
                  <div className="checkbox-group">
                    <input
                      type="checkbox"
                      checked={toBool(wisser.routecontact)}
                      onChange={(e) => updateWisserData(index, 'routecontact', e.target.checked)}
                    />
                    <span>{boolToJaNee(toBool(wisser.routecontact))}</span>
                  </div>
                </td>
                <td>
                  <div className="checkbox-group">
                    <input
                      type="checkbox"
                      checked={toBool(wisser.nog_in_dienst)}
                      onChange={(e) => updateWisserData(index, 'nog_in_dienst', e.target.checked)}
                    />
                    <span>{boolToJaNee(toBool(wisser.nog_in_dienst))}</span>
                  </div>
                </td>
                <td>
                  <input
                    type="text"
                    className="form-control"
                    value={wisser.opmerkingen || ''}
                    onChange={(e) => updateWisserData(index, 'opmerkingen', e.target.value)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      <div style={{ marginTop: '20px' }}>
        <button className="btn btn-primary" onClick={saveInspectie} disabled={loading}>
          {loading ? 'Opslaan...' : 'Inspectie Opslaan'}
        </button>
        <button className="btn btn-secondary" onClick={generatePDF} disabled={loading} style={{ marginLeft: '10px' }}>
          {loading ? 'Genereren...' : 'PDF Genereren'}
        </button>
      </div>
    </div>
  );

  const renderTodoList = () => (
    <div className="card">
      <h2>To-Do Lijsten</h2>

      <div className="card">
        <h3>Service To-Do's</h3>
        <ul>
          {todoList.map((todo, index) => (
            <li key={index} style={{ textDecoration: todo.done ? 'line-through' : 'none' }}>
              <input
                type="checkbox"
                checked={todo.done}
                onChange={(e) => {
                  const newList = [...todoList];
                  newList[index].done = e.target.checked;
                  setTodoList(newList);
                }}
              />
              {todo.text}
            </li>
          ))}
        </ul>
      </div>

      <div className="card">
        <h3>Klantenservice To-Do's</h3>
        <ul>
          {klantenserviceTodoList.map((todo, index) => (
            <li key={index} style={{ textDecoration: todo.done ? 'line-through' : 'none' }}>
              <input
                type="checkbox"
                checked={todo.done}
                onChange={(e) => {
                  const newList = [...klantenserviceTodoList];
                  newList[index].done = e.target.checked;
                  setKlantenserviceTodoList(newList);
                }}
              />
              {todo.text}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );

  const renderRapport = () => (
    <div className="card">
      <h2>Rapport</h2>
      <div id="rapport-content">
        <h1>Lavans Service Rapport</h1>
        <p><strong>Relatienummer:</strong> {formData.relatienummer}</p>
        <p><strong>Klantnaam:</strong> {formData.klantnaam}</p>
        <p><strong>Contactpersoon:</strong> {formData.contactpersoon}</p>
        <p><strong>Inspecteur:</strong> {formData.inspecteur}</p>
        <p><strong>Datum:</strong> {formData.datum}</p>
        <p><strong>Tijd:</strong> {formData.tijd}</p>

        <h3>Algemeen</h3>
        <p><strong>Opmerkingen:</strong> {formData.algemeen_values.opmerkingen || '-'}</p>

        <h3>Matten</h3>
        <p><strong>Concurrenten matten:</strong> {mattenConcurrenten.andere_mat_aanwezig}</p>
        {mattenConcurrenten.andere_mat_aanwezig === 'Ja' && (
          <p><strong>Concurrent:</strong> {mattenConcurrenten.andere_mat_concurrent} ({mattenConcurrenten.aantal_concurrent} stuks)</p>
        )}
        <p><strong>Aantal koop matten:</strong> {mattenConcurrenten.aantal_koop}</p>

        <table className="table">
          <thead>
            <tr>
              <th>Productnummer</th>
              <th>Locatie</th>
              <th>Aanwezig</th>
              <th>Schoon/Onbeschadigd</th>
              <th>Leeftijd</th>
              <th>Opmerkingen</th>
            </tr>
          </thead>
          <tbody>
            {mattenData.map((mat, index) => (
              <tr key={index}>
                <td>{mat.productnummer}</td>
                <td>{mat.locatie}</td>
                <td>{boolToJaNee(toBool(mat.aanwezig))}</td>
                <td>{boolToJaNee(toBool(mat.schoon_onbeschadigd))}</td>
                <td>{berekenLeeftijd(mat.barcode)}</td>
                <td>{mat.opmerkingen || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>

        <h3>Wissers</h3>
        <p><strong>Concurrenten wissers:</strong> {wissersConcurrenten.wissers_concurrent}</p>
        {wissersConcurrenten.wissers_concurrent === 'Ja' && (
          <p><strong>Concurrent:</strong> {wissersConcurrenten.wissers_concurrent_toelichting}</p>
        )}
        <p><strong>Andere schoonmaakmiddelen:</strong> {wissersConcurrenten.andere_zaken || '-'}</p>

        <table className="table">
          <thead>
            <tr>
              <th>Productnummer</th>
              <th>Locatie</th>
              <th>Aanwezig</th>
              <th>Schoon/Onbeschadigd</th>
              <th>Routecontact</th>
              <th>Nog in dienst</th>
              <th>Opmerkingen</th>
            </tr>
          </thead>
          <tbody>
            {wissersData.map((wisser, index) => (
              <tr key={index}>
                <td>{wisser.productnummer}</td>
                <td>{wisser.locatie}</td>
                <td>{boolToJaNee(toBool(wisser.aanwezig))}</td>
                <td>{boolToJaNee(toBool(wisser.schoon_onbeschadigd))}</td>
                <td>{boolToJaNee(toBool(wisser.routecontact))}</td>
                <td>{boolToJaNee(toBool(wisser.nog_in_dienst))}</td>
                <td>{wisser.opmerkingen || '-'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );

  const renderOpgeslagenInspecties = () => (
    <div className="card">
      <h2>Opgeslagen Inspecties</h2>
      {savedInspecties.length === 0 ? (
        <p>Geen opgeslagen inspecties gevonden.</p>
      ) : (
        <div>
          <button className="btn btn-danger" onClick={clearData} style={{ marginBottom: '20px' }}>
            Alle Data Wissen
          </button>
          <table className="table">
            <thead>
              <tr>
                <th>Datum</th>
                <th>Relatienummer</th>
                <th>Klantnaam</th>
                <th>Inspecteur</th>
                <th>Acties</th>
              </tr>
            </thead>
            <tbody>
              {savedInspecties.map((inspectie, index) => (
                <tr key={index}>
                  <td>{inspectie.datum}</td>
                  <td>{inspectie.relatienummer}</td>
                  <td>{inspectie.klantnaam}</td>
                  <td>{inspectie.inspecteur}</td>
                  <td>
                    <button 
                      className="btn btn-secondary" 
                      onClick={() => {
                        setFormData({
                          relatienummer: inspectie.relatienummer,
                          klantnaam: inspectie.klantnaam,
                          contactpersoon: inspectie.contactpersoon,
                          contact_email: inspectie.contact_email,
                          inspecteur: inspectie.inspecteur,
                          datum: inspectie.datum,
                          tijd: inspectie.tijd,
                          algemeen_values: inspectie.algemeen_values || {}
                        });
                        setMattenData(inspectie.matten_data || []);
                        setWissersData(inspectie.wissers_data || []);
                        if (inspectie.matten_concurrenten) {
                          setMattenConcurrenten(inspectie.matten_concurrenten);
                        }
                        if (inspectie.wissers_concurrenten) {
                          setWissersConcurrenten(inspectie.wissers_concurrenten);
                        }
                        setActiveTab('inspectie');
                      }}
                    >
                      Laden
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );

  return (
    <div className="App">
      <div className="header">
        <h1>Lavans Service App</h1>
        <p>Ideaal Servicemoment</p>
      </div>

      <div className="container">
        {message && (
          <div className={`alert alert-${messageType}`}>
            {message}
          </div>
        )}

        <div className="tabs">
          <button
            className={`tab ${activeTab === 'inspectie' ? 'active' : ''}`}
            onClick={() => setActiveTab('inspectie')}
          >
            Inspectie
          </button>
          <button
            className={`tab ${activeTab === 'todos' ? 'active' : ''}`}
            onClick={() => setActiveTab('todos')}
          >
            To-Do's
          </button>
          <button
            className={`tab ${activeTab === 'rapport' ? 'active' : ''}`}
            onClick={() => setActiveTab('rapport')}
          >
            Rapport
          </button>
          <button
            className={`tab ${activeTab === 'opgeslagen' ? 'active' : ''}`}
            onClick={() => setActiveTab('opgeslagen')}
          >
            Opgeslagen
          </button>
        </div>

        {activeTab === 'inspectie' && renderInspectieForm()}
        {activeTab === 'todos' && renderTodoList()}
        {activeTab === 'rapport' && renderRapport()}
        {activeTab === 'opgeslagen' && renderOpgeslagenInspecties()}

        {showModal && (
          <div className="modal">
            <div className="modal-content">
              <div className="modal-header">
                <h3>{modalTitle}</h3>
                <button className="modal-close" onClick={() => setShowModal(false)}>
                  ×
                </button>
              </div>
              <div dangerouslySetInnerHTML={{ __html: modalContent }} />
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
